<?phpclass Calculadora{	public $valorUm;	public $valorDois;	public $operador;			function __construct($valorUm, $valorDois, $operador){		$this->valorUm = $valorUm;		$this->valorDois = $valorDois;		$this->operador = $operador;	}function Calcular(){	if ($this->operador == "soma"){		$resultado = $this->valorUm + $this->valorDois;	}		if ($this->operador == "subtracao"){		$resultado = $this->valorUm - $this->valorDois;	}		if ($this->operador == "multiplicacao"){		$resultado = $this->valorUm * $this->valorDois;	}		if ($this->operador == "divisao"){		$resultado = $this->valorUm / $this->valorDois;	}		return $resultado;	}}?>